import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../app/cartSlice';

const Cart = () => {
  const cart = useSelector((state) => state.cart.items);
  const dispatch = useDispatch();

  return (
    <div className="cart-page" style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ textAlign: 'center', color: '#444', marginBottom: '20px' }}>Your Cart</h2>
      {cart.length === 0 ? (
        <p style={{ textAlign: 'center', fontSize: '18px', color: '#777' }}>Your cart is empty</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: '0' }}>
          {cart.map((item) => (
            <li
              key={item.id}
              style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                padding: '15px',
                marginBottom: '10px',
                border: '1px solid #ddd',
                borderRadius: '8px',
                backgroundColor: '#f9f9f9',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
              }}
            >
              <div style={{ maxWidth: '60%' }}>
                <h4 style={{ marginBottom: '10px', color: '#333' }}><u>{item.title}</u></h4>
                <p style={{ color: '#555', fontSize: '16px' }}>Price: <strong>${item.price}</strong></p>
              </div>
              <img
                src={item.image}
                alt={item.title}
                style={{
                  height: '100px',
                  objectFit: 'contain',
                  borderRadius: '8px',
                  border: '1px solid #ddd',
                  marginRight: '15px',
                }}
              />
              <button
                onClick={() => dispatch(removeFromCart(item.id))}
                style={{
                  backgroundColor: 'cadetblue',
                  color: 'white',
                  borderRadius: '25px',
                  padding: '8px 20px',
                  border: 'none',
                  cursor: 'pointer',
                  fontWeight: 'bold',
                  boxShadow: '0 3px 5px rgba(0, 0, 0, 0.1)',
                  transition: 'background-color 0.3s ease',
                }}
                onMouseOver={(e) => (e.target.style.backgroundColor = '#3e8e41')}
                onMouseOut={(e) => (e.target.style.backgroundColor = 'cadetblue')}
              >
                Remove Item
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Cart;
